package automation;

public class Primitives 
{
	
	public static void main(String[] args) 
	
	{
		// TODO Auto-generated method stub
		
		//Variable declaration
		String bikeName="Avenger";
		int bikeSpeed=150;
		double bikeWeight=155.5d;
		boolean isDiscbreaktype=true;
		char bikeFirstletter='A';
		byte bikeChainteeth=127;
		float bikeLoaninterestrate=10.95f;
		short bikeDrivenkilometers=32767;
		
		System.out.println("BikeName ="+bikeName);
		System.out.println("Speed ="+bikeSpeed);
		System.out.println("Weight ="+bikeWeight);
		System.out.println("Disc Break Model ="+isDiscbreaktype);
		System.out.println("First Letter of Bike ="+bikeFirstletter);
		System.out.println("Total Teeth in chain ="+bikeChainteeth);
		System.out.println("Availed Loan interest Rate ="+bikeLoaninterestrate);
		System.out.println("Kilometers after Roaming ="+bikeDrivenkilometers);
		
		
	}

}
